import { cn } from "@/lib/utils";
import * as React from "react";

interface SpeedBackgroundProps extends React.ComponentProps<"div"> {
  color?: string;
  count?: number;
  speed?: number;
}

const SpeedBackground = ({
  className,
  color = "hsl(var(--primary))",
  count = 18,
  speed = 1,
  ...props
}: SpeedBackgroundProps) => {
  const getKeyframes = React.useCallback(() => {
    return `@layer utilities {
  @keyframes speed-line {
    0% {
      transform: scaleX(0);
      opacity: 0.1;
    }
    20% {
      opacity: 0.7;
    }
    90% {
      opacity: 0.7;
    }
    100% {
      transform: scaleX(1);
      opacity: 0.1;
    }
  }

  .animate-speed-line {
    animation: speed-line linear infinite;
    transform-origin: 0% 50%;
  }
}`;
  }, [color, count, speed]);

  return (
    <>
      <style>{getKeyframes()}</style>
      <div
        className={cn(
          "pointer-events-none absolute inset-0 z-0 overflow-hidden",
          className,
        )}
        {...props}
      >
        <div className="absolute left-1/2 top-1/2">
          {Array.from({ length: count }).map((_, i) => {
            const angle = (360 / count) * i;
            const duration = 20 / speed + (Math.random() * 10) / speed;
            const delay = (Math.random() * 20) / speed;

            return (
              <div
                key={i}
                className="absolute"
                style={{
                  transform: `rotate(${angle}deg)`,
                  transformOrigin: "0% 50%",
                }}
              >
                <div
                  className="h-px w-screen animate-speed-line"
                  style={{
                    backgroundImage: `linear-gradient(to right, transparent, ${color}, transparent)`,
                    animationDuration: `${duration}s`,
                    animationDelay: `${delay}s`,
                  }}
                />
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
};

export default SpeedBackground;
