"use client";

import { useScrollPosition } from "@/components/targetblank/hooks/use-scroll-position";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  ArrowDownIcon,
  ArrowLeftIcon,
  ArrowRightIcon,
  ArrowUpIcon,
} from "lucide-react";
import * as React from "react";

const ScrollPositionDemo: React.FC = () => {
  const { x, y, direction } = useScrollPosition({ throttleDelay: 50 });

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const scrollToBottom = () => {
    window.scrollTo({ top: document.body.scrollHeight, behavior: "smooth" });
  };

  const scrollToLeft = () => {
    window.scrollTo({ left: 0, behavior: "smooth" });
  };

  const scrollToRight = () => {
    window.scrollTo({ left: document.body.scrollWidth, behavior: "smooth" });
  };

  return (
    <div className="space-y-6">
      <Card className="max-w-md mx-auto">
        <CardHeader>
          <CardTitle>Scroll Position Demo</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div className="text-center p-3 bg-muted rounded-lg">
                <div className="text-2xl font-bold">{x}</div>
                <div className="text-sm text-muted-foreground">X Position</div>
                {direction.horizontal && (
                  <div className="flex items-center justify-center mt-1">
                    {direction.horizontal === "left" ? (
                      <ArrowLeftIcon className="size-4 text-blue-500" />
                    ) : (
                      <ArrowRightIcon className="size-4 text-blue-500" />
                    )}
                  </div>
                )}
              </div>
              <div className="text-center p-3 bg-muted rounded-lg">
                <div className="text-2xl font-bold">{y}</div>
                <div className="text-sm text-muted-foreground">Y Position</div>
                {direction.vertical && (
                  <div className="flex items-center justify-center mt-1">
                    {direction.vertical === "up" ? (
                      <ArrowUpIcon className="size-4 text-green-500" />
                    ) : (
                      <ArrowDownIcon className="size-4 text-green-500" />
                    )}
                  </div>
                )}
              </div>
            </div>

            <div className="flex flex-col gap-2">
              <Button onClick={scrollToTop} variant="outline" size="sm">
                Scroll to Top
              </Button>
              <Button onClick={scrollToBottom} variant="outline" size="sm">
                Scroll to Bottom
              </Button>
              <Button onClick={scrollToLeft} variant="outline" size="sm">
                Scroll to Left
              </Button>
              <Button onClick={scrollToRight} variant="outline" size="sm">
                Scroll to Right
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Content to enable scrolling */}
      <div className="space-y-4">
        <div className="h-96 w-full bg-gradient-to-b from-blue-100 to-purple-100 rounded-lg p-4">
          <h3 className="text-lg font-semibold mb-2">Scroll Content</h3>
          <p className="text-sm text-muted-foreground">
            Scroll up and down, or left and right to see the position values
            change. The arrows indicate the scroll direction.
          </p>
        </div>

        {Array.from({ length: 10 }, (_, i) => (
          <div
            key={i}
            className="h-32 w-full bg-gradient-to-r from-green-100 to-blue-100 rounded-lg p-4"
          >
            <h4 className="font-medium">Section {i + 1}</h4>
            <p className="text-sm text-muted-foreground">
              This is section {i + 1} to provide scrollable content.
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ScrollPositionDemo;
